class EadXml < ActiveFedora::NokogiriDatastream
  set_terminology do |t|
    t.root(:path=>'ead', :xmlns=>"urn:isbn:1-931666-00-8", :schema=>"urn:isbn:1-931666-00-8 http://www.loc.gov/ead/ead.xsd")
    
    t.person_ref(:path=>'persname'){
      t.person_role(:path=>{:attribute=>"role"})
      t.person_name(:path=>{:attribute=>"normal"})
    }
    t.p_ref(:path=>'p')
    t.eadid_ref(:path=>'eadheader'){
      t.eadid
    }
    t.physdesc_ref(:path=>'physdesc'){
      t.dimensions
    }
    t.scopecontent_ref(:path=>'scopecontent'){
      t.scope_p(:ref=>[:p_ref])
    }
    t.controlaccess_ref(:path=>'controlaccess'){
      t.genreform
    }
    t.acqinfo_ref(:path=>'acqinfo'){
      t.acqinfo_p(:ref=>[:p_ref])
    }
    t.odd_ref(:path=>'odd'){
      t.odd_p(:ref=>[:p_ref])
    }
    t.daogrp_ref(:path=>'daogrp'){
      t.daoloc{
        t.daoloc_href(:path=>{:attribute=>"href"})
      }
    }
    t.unit_title_ref(:path=>'unittitle'){
      t.label(:path=>{:attribute=>"label"})
      t.encoding(:path=>{:attribute=>"encodinganalog"})
      t.imprint{
        t.geogname
        t.publisher
      }
      t.unit_date_ref(:path=>'unitdate'){
        t.era(:path=>{:attribute=>"era"})
        t.calendar(:path=>{:attribute=>"label"})
      }
      t.num{
        t.num_type(:path=>{:attribute=>"type"})
      }
    }

    t.did_ref(:path=>'did'){
      t.head
      t.unitid
      t.origination{
        t.person_name(:ref=>[:person_ref])
      }
      t.unittitle(:ref=>[:unit_title_ref])      
      t.physdesc(:ref=>[:physdesc_ref])
    }

    t.archive_desc(:path=>'archdesc'){
      t.level(:path=>{:attribute =>"level"})
      t.related_encoding(:path=>{:attribute =>"relatedencoding"})
      t.did{
        t.head
        t.unittitle(:ref=>[:unit_title_ref])
        t.archive_unit_id(:path=>'unitid'){
          t.encoding(:path=>{:attribute=>"encodinganalog"})
          t.country_code(:path=>{:attribute=>"countrycode"})
          t.repository_code(:path=>{:attribute=>"repositorycode"})
        }
        t.archive_unit_date(:path=>'unitdate'){
          t.type_(:path=>{:attribute=>"type"})
          t.range(:path=>{:attribute=>"normal"})
        }
        t.archive_lang(:path=>'langmaterial'){
          t.label(:path=>{:attribute=>"label"})
          t.language_{
            t.language_code(:path=>{:attribute=>"langcode"})
          }
        }

        t.archive_repository(:path=>'repository'){
          t.label(:path=>{:attribute=>"label"})
          t.encoding(:path=>{:attribute=>"encodinganalog"})
          t.corpname{
            t.subarea
          }
          t.address{
            t.addressline
          }
        }
      }

      t.archive_access(:path=>'accessrestrict'){
        t.encoding(:path=>{:attribute=>"encodinganalog"})
        t.heading(:path=>'head')
        t.p(:ref=>[:p_ref])
      }

      t.archive_acq(:path=>'acqinfo'){
        t.encoding(:path=>{:attribute=>"encodinganalog"})
        t.heading(:path=>'head')
        t.p(:ref=>[:p_ref])
      }

      t.archive_citation(:path=>'prefercite'){
        t.encoding(:path=>{:attribute=>"encodinganalog"})
        t.heading(:path=>'head')
        t.p(:ref=>[:p_ref])
      }

      t.dsc{
        t.type_(:path=>{:attribute =>"type"})
        t.type_heading(:path=>'head')
        t.sub_collection(:path=>'c01'){
          t.sub_collection_level(:path=>{:attribute =>"level"})
          t.did(:ref=>[:did_ref])
          t.scopecontent(:ref=>[:scopecontent_ref])
          t.odd(:ref=>[:odd_ref])
          t.controlaccess(:ref=>[:controlaccess_ref])
          t.item(:path=>'c02'){
          	t.did(:ref=>[:did_ref])
          	t.scopecontent(:ref=>[:scopecontent_ref])
            t.odd(:ref=>[:odd_ref])
          	t.controlaccess(:ref=>[:controlaccess_ref])
          	t.acqinfo(:ref=>[:acqinfo_ref])
          	t.daogrp(:ref=>[:daogrp_ref])
          }
        }
      }
    }
    t.ead_header(:ref => [:eadid_ref])

  end
  def self.xml_template
      builder = Nokogiri::XML::Builder.new do |t|
          t.ead(:version=>"1.0", "xmlns:xlink"=>"http://www.w3.org/1999/xlink",
              "xmlns:xsi"=>"http://www.w3.org/2001/XMLSchema-instance",
              "xmlns"=>"urn:isbn:1-931666-00-8",
              "xsi:schemaLocation"=>"urn:isbn:1-931666-00-8 http://www.loc.gov/ead/ead.xsd"){
              
              t.eadheader{
                t.eadid
                t.filedesc{
                  t.titlestmt{
                    t.titleproper
                    t.author
                  }
                  t.publicationstmt{
                    t.publisher
                    t.address{
                      t.addressline
                    }
                  }
                }
                t.profiledesc{
                  t.creation{
                    t.date
                  }
                  t.langusage{
                    t.language
                  }
                }
              }
              t.frontmatter{
                t.titlepage{
                  t.titleproper
                }
              }
              t.archdesc(:level=>"collection"){
                t.did{
                  t.head
                  t.unittitle
                  t.unitid
                  t.unitdate
                  t.langmaterial{
                    t.language
                  }
                  t.repository{
                    t.corpname{
                      t.subarea
                    }
                    t.address{
                      t.addresslin
                    }
                  }
                }
                t.accessrestrict{
                  t.head
                  t.p
                }
                t.acqinfo{
                  t.head
                  t.p
                }
                t.prefercite{
                  t.head
                  t.p
                }
                t.dsc{
                  t.head
                  t.c01(:level=>"item"){
                    t.did{
                      t.unitid
                      t.origination{
                        t.persname
                      }
                      t.unittitle{
                        t.imprint{
                          t.geogname
                          t.publisher
                        }
                        t.unitdate
                      }
                      t.physdesc
                    }
                    t.scopecontent{
                      t.p
                    }
                    t.controlaccess{
                      t.genreform
                    }
                    t.c02{
                      t.did{
                        t.unitid
                        t.origination{
                          t.persname
                        }
                        t.unittitle{
                          t.num
                        }
                        t.physdesc{
                          t.dimensions
                        }
                      }
                      t.scopecontent{
                        t.p
                      }
                      t.odd{
                        t.p
                      }
                      t.controlaccess{
                        t.genreform
                      }
                      t.acqinfo{
                        t.p
                      }
                      t.daogrp{
                        t.daoloc(:href=>"")
                      }
                    }
                  }
                }
              }
          }
        end
        return builder.doc
    end
end